mapper.select:
SELECT notice_no AS noticeNo ,title AS title ,TO_CHAR(created_date, 'YYYY/MM/DD') AS createdDate ,TO_CHAR(updated_date, 'YYYY/MM/DD') AS updatedDate ,user_id AS userId ,content AS content FROM ( SELECT /*+ index_desc(tbl_ticket SYS_C0021886) */ -- 인덱스 힌트 rownum rn ,notice_no ,title ,created_date ,updated_date ,user_id ,content FROM tbl_notice WHERE rownum <= #{pageNum} * #{amount}
AND title LIKE '%'||#{keyword}||'%'
AND content LIKE '%'||#{keyword}||'%'
AND user_id LIKE '%'||#{keyword}||'%'
) WHERE rn BETWEEN ((#{pageNum} - 1) * #{amount}) AND (#{pageNum} * #{amount})

mapper.insert:
   		INSERT INTO tbl_notice
   		(
   			 notice_no
   			,title
   			,created_date
   			,updated_date
   			,user_id
   			,content
   		) VALUES (
   			 seq_notice.nextval
   			,#{title}
   			,TO_DATE(#{createdDate},'YYYY/MM/DD')
		    ,TO_DATE(#{createdDate},'YYYY/MM/DD')
   			,#{userId}
   			,#{content}
   		)

mapper.update:
UPDATE tbl_notice
   SET title = #{title}
      ,created_date = #{createdDate}
      ,updated_date = #{updatedDate}
      ,content = #{content}
 WHERE notice_no = #{noticeNo}
